import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_URL || 'http://localhost:3001', // Your Express server runs on 3001
});

export const sendMessage = async (messageData) => {
  try {
    const response = await api.post('/api/contact', messageData); // Updated endpoint
    return response.data;
  } catch (error) {
    throw error;
  }
};
