import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import Hero from './components/Hero';
import About from './components/About';
import Projects from './components/Projects';  // Ensure correct import
import Skills from './components/Skills';
import Contact from './components/Contact';
import Footer from './components/Footer';
import './styles/main.css';
import { useEffect } from 'react'; 
import { sendLocationData } from './services/api';

function App() {
  useEffect(() => {
    const fetchAndSendGeoLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          async (position) => {
            const { latitude, longitude } = position.coords;
            const locationData = { latitude, longitude };
            console.log('Fetched geolocation data:', locationData);
            try {
              const result = await sendLocationData(locationData);
              console.log('Location data saved:', result);
            } catch (error) {
              console.error('Failed to save location:', error);
            }
          },
          (error) => {
            console.error('Error getting geolocation:', error.message);
            // Handle different error cases
            switch(error.code) {
              case error.PERMISSION_DENIED:
                console.error("User denied the request for Geolocation.");
                break;
              case error.POSITION_UNAVAILABLE:
                console.error("Location information is unavailable.");
                break;
              case error.TIMEOUT:
                console.error("The request to get user location timed out.");
                break;
              case error.UNKNOWN_ERROR:
                console.error("An unknown error occurred.");
                break;
            }
          },
          {
            enableHighAccuracy: true,
            timeout: 5000,
            maximumAge: 0
          }
        );
      } else {
        console.warn('Geolocation is not supported by this browser.');
      }
    };

    fetchAndSendGeoLocation();
  }, []);
  return (
    <Router>
      <div className="App">
        <Header />
        <main>
          <Routes>
            <Route path="/" element={
              <>
                <Hero />
                <About />
                <Projects />  
                <Skills />
                <Contact />
              </>
            } />
          </Routes>
        </main>
        <Footer />
      </div>
    </Router>
  );
}

export default App;
