import axios from 'axios';
// https://portfolio-r-gzgf.onrender.com
const api = axios.create({
  baseURL: 'http://localhost:3001/', 
  timeout: 5000,
});
export const sendMessage = async (messageData) => {
  try {
    const response = await api.post('/api/contact', messageData);
    if (response.data.success) {
      return response.data;
    }
    throw new Error(response.data.error || 'Message submission failed');
  } catch (error) {
    if (error.response) {
      throw new Error(error.response.data.error || 
                     error.response.data.details || 
                     'Server error');
    } else if (error.request) {
      throw new Error('Cannot connect to server. Please try again later.');
    } else {
      throw new Error('Request failed: ' + error.message);
    }
  }
};

export const sendLocationData = async (locationData) => {
  try {
    const response = await api.post(`/api/location`, locationData);
    return response.data;
  } catch (error) {
    console.error('Error sending location data:', error.response ? error.response.data : error.message);
    // You might not want to throw an error here, as fetching location might fail silently.
    console.error('Failed to send location data to backend.');
  }
};
