import { useState } from "react";
import "../styles/main.css";

function CodingProfiles() {
  const [activeProfile, setActiveProfile] = useState(null);

  const profiles = {
    leetcode: "https://leetcode.com/u/Sumukesh/",
    codeforces: "https://codeforces.com/profile/Sumukesh",
  };

  return (
    <div className="profiles-container">
      <h1>Coding Profiles</h1>

      <div className="buttons">
        <button onClick={() => setActiveProfile("leetcode")}>
          Open LeetCode
        </button>

        <button onClick={() => setActiveProfile("codeforces")}>
          Open Codeforces
        </button>
      </div>

      {activeProfile && (
        <div className="modal-overlay">
          <div className="modal-box">
            <div className="modal-header">
              <h2>
                {activeProfile === "leetcode"
                  ? "LeetCode Profile"
                  : "Codeforces Profile"}
              </h2>

              <button
                className="close-btn"
                onClick={() => setActiveProfile(null)}
              >
                âœ–
              </button>
            </div>

            {/* TRY EMBEDDING FIRST */}
            <iframe
              src={profiles[activeProfile]}
              title="Profile"
              className="profile-frame"
            />

            {/* Fallback Button */}
            <div className="fallback">
              <button
                onClick={() =>
                  window.open(
                    profiles[activeProfile],
                    "_blank",
                    "noopener,noreferrer"
                  )
                }
              >
                Open in New Tab
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default CodingProfiles;
